					type: "GET",
					method: "frappe.desk.tags.get_tagged_docs",
					args: {
						"doctype": "Supplier",
						"tag": args.tag
					},
					callback: load_suppliers
				});
			} else if (args.supplier_type) {
				return frappe.call({
					method: "frappe.client.get_list",
					args: {
						doctype: "Supplier",
						order_by: "name",
						fields: ["name"],
						filters: [["Supplier", "supplier_type", "=", args.supplier_type]]

					},
					callback: load_suppliers
				});
			}
		});
		dialog.show();

	},
